import React, { useState, useEffect } from 'react'

import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'


export default function AddDialog ({handleCloseDialog, open, handleCloseAndSave}){
    const [car, setCar] = useState({
        brand: "",
        model: "",
        price: '',
        year: ''
    })
    const handleChange=(event)=>{
        setCar({...car, [event.target.name]:event.target.value});
    }

    const handleSubmit = () =>{
        handleCloseAndSave(car);
        setCar({
            brand: "",
            model: "",
            price: '',
            year: ''
        });
    }
    return(
        <Dialog onClose={handleCloseDialog} open={open} className="dialog">
            <DialogTitle id="dialogTitle">Sell your Car</DialogTitle>
            <DialogContent className="addDialogContent">
                <div className="addDialogEntries">


                            <TextField className="addDialogEntry" id="name" label="Brand" name="bookName" variant="outlined" value={car.brand} onChange={handleChange} />
                            <TextField className="addDialogEntry" label="Model" name="writer" variant="outlined" value={car.model} onChange={handleChange} />





                        <TextField className="addDialogEntryBelow" label=" year" name="releaseYear" variant="outlined" value={car.year} onChange={handleChange} />
                        <TextField className="addDialogEntryBelow" label="Price" name="genre" variant="outlined" value={car.price} onChange={handleChange} />

                </div>
            </DialogContent>
            <DialogActions className='dialogActions'>
                <Button onClick={handleCloseDialog} color="secondary" variant="contained" autoFocus>
                    Close
                </Button>
                <Button onClick={handleSubmit} color="primary" variant="contained">Submit</Button>
            </DialogActions>
        </Dialog>
    )
}
