import React, { useState, useEffect } from 'react'

import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import './dialog.css'
import picture from './imgs/carPhoto.jpg'


export default function ItemDialog ({handleCloseDialog, open, openedItem}){
    const [car, setCar] = useState({
        brand: "",
        model: "",
        price: "",
        year: ""
    })
    const handleChange=(event)=>{
        setCar({...car, [event.target.name]:event.target.value});
    }

    const handleSubmit = () =>{
        handleCloseAndSave(car);
        setCar({
            brand: "",
            model: "",
            price: '',
            year: ''
        });

    }
    return(
        <Dialog onClose={handleCloseDialog} open={open} className="itemDialog">
            <DialogContent className="itemDialogContent">
                <div className='picNamePrice'>
                    <img src={picture} alt='carPicture'></img>
                    <h2>{openedItem.brand} {openedItem.model}</h2>
                    <h3>{openedItem.price}</h3>
                </div>
            </DialogContent>
            <DialogActions className='dialogActions'>

            </DialogActions>
        </Dialog>
    )
}
