import React, { useEffect, useState, useContext } from "react";
import './search.css'
import menuItem from './imgs/menuIcon.png'
import Popover from '@material-ui/core/Popover';
import OutlinedInput from '@material-ui/core/OutlinedInput';


export default function SearchAndCategory({searchParam, handleChange}){


    const [anchorEl, setAnchorEl] = useState();
    const logoutPop = anchorEl ? Boolean(anchorEl) : undefined;
    const id = logoutPop ? 'logout-popover' : undefined;
    const [loggedUser, setLoggedUser] = useState({
        username:'',
        id:0
    })

    const handleCloseLogin = () =>{
        setAnchorEl(null);
    }

    const handleOpenLogin = (event) =>{
        setAnchorEl(event.currentTarget);
    }

    const handleUsernameChange = (prop) => (event) =>{
        setLoggedUser({...loggedUser, [prop]: event.target.value});
    }

    return(
        <div id='searchBar'>
            <div id='category'>
                <img src={menuItem} alt='menuItem' id='menuItem'></img>
                <p>Cars by Category</p>
            </div>
            <div id='searchBox'>
                <input type="text" id="searchField" placeholder='Search Cars' value={searchParam} onChange={handleChange}></input>
            </div>
            <div id='loginButton'>
                <p className='SearchB' onClick={handleOpenLogin}>Login</p>
                <p className='SearchB'>Sign Up</p>
                <Popover
                id={id}
                open={logoutPop}
                anchorEl={anchorEl}
                onClose={handleCloseLogin}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                }}
            >
                <div className='loginCredentialsContainer'>
                    <h4>Log In</h4>
                <OutlinedInput
                    id='username'
                    value={loggedUser.username}
                    handleChange={handleUsernameChange('username')}
                    label='Username'

                />
                <OutlinedInput
                    id='password'
                    label='Password'
                    type='password'
                />
                </div>

            </Popover>

            </div>
        </div>
    )
}
